import React from 'react';
import {Route} from 'react-router-dom';
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from "../collection/collection.component";
import {convertCollectionsSnapshotToMap, firestore} from "../../firebase/firebase.util";
import {connect} from 'react-redux';
import {createStructuredSelector} from "reselect";
import {fetchCollectionsStartAsync, updateCollections} from "../../redux/shop/shop.actions";
import WithSpinner from "../../components/with-spinner/with-spinner.component";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

    unsubscribeFromCollectionsSnapshot = null;

    state = {
        isLoading: false
    };

    componentDidMount() {
        fetchCollectionsStartAsync()
    }

    render() {
        const {match} = this.props;
        return (<div className='shop-page'>
            <Route exact path={`${match.path}`}
                   render={props => (<CollectionsOverviewWithSpinner {...props}/>)}/>
            <Route path={`${match.path}/:collectionId`}
                   render={props => (<CollectionPageWithSpinner {...props}/>)}/>
        </div>);
    }
}

const mapDispatchToProps = dispatch => ({
    updateCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
});

export default connect(null, mapDispatchToProps)(ShopPage);
