import React from 'react';
import {connect} from 'react-redux';
import {createStructuredSelector} from "reselect";

import {auth} from '../../firebase/firebase.util';

import {ReactComponent as Logo} from '../../assets/crown.svg';
import CartIcon from "../cart-icon/cart-icon.component";
import CartDropdown from "../cart-dropdown/cart-dropdown.component";
import {selectCurrentUser} from '../../redux/user/user.selector'
import {selectIsHidden} from '../../redux/cart/cart.selectors'
import {HeaderContainer, LogoContainer, OptionDiv, OptionLink, OptionsContainer} from "./header.styles";

const Header = ({ currentUser, isCartHidden }) => (
    <HeaderContainer>
        <LogoContainer to='/'>
            <Logo className='logo' />
        </LogoContainer>
        <OptionsContainer>
            <OptionLink className='option' to='/shop'>SHOP</OptionLink>
            <OptionLink className='option' to='/shop'>CONTACT</OptionLink>
            {
                currentUser ?
                    <OptionDiv className='option' onClick={ () => auth.signOut()}>SIGN OUT</OptionDiv>
                    :
                    <OptionLink className='option' to='/signIn'>SIGN IN</OptionLink>
            }
            <CartIcon/>
        </OptionsContainer>
        {isCartHidden ? null : <CartDropdown/>}


    </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    isCartHidden: selectIsHidden
});

export default connect(mapStateToProps)(Header);
